import React from "react";
import classes from './FoodDetails.module.css';
import {Link,useLocation} from 'react-router-dom';
import {useState} from 'react'
import { useSelector } from "react-redux";
import {useEffect} from 'react';

const FoodDetails = ()=>{

    const [filteredFoods,setFilteredFoods] = useState([])

    const location = useLocation()

    console.log(location.pathname.split('/')[2])
    const foodEnpoint = location.pathname.split('/')[2]
    const {token} = useSelector((state)=> state.auth)


    useEffect(()=>{

        const fetchFoodType = async()=>{

            const res = await fetch(`http://localhost:9000/product?category=${foodEnpoint}`,{

            headers: {
                'Authorization':`Bearer ${token}`
            }

            })

            const data = await res.json()
            setFilteredFoods(data)
        }

        fetchFoodType()

    },[foodEnpoint])

    return(

        <div className={classes.container}>
            <div className={classes.wrapper}>
               {filteredFoods?.length !== 0 && <h2 className={classes.title}>The best {foodEnpoint} in the register</h2>}

                   <div className={classes.foods}>
                    {filteredFoods.length !== 0 ? filteredFoods.map((f)=>{

                        <Link to={`/food/${f._id}`} key={f._id} className={classes.food}>

                        </Link>
                    }): <h1 className={classes.noQuantity}>No{foodEnpoint}right now</h1>
                }

                   </div>
            </div>

        </div>
    )
}
export default FoodDetails
