import React from "react";
import classes from './FoodDetails.module.css';
import { Link, useLocation, useParams } from 'react-router-dom';
import { useState } from 'react'
import { useSelector } from "react-redux";
import { useEffect } from 'react';
import { addProduct } from '../../ReactRedux/authSlice'
import {AiOutlineShoppingCart} from 'react-icons/ai'

const FoodDetails = () => {

    const [filteredFoods, setFilteredFoods] = useState([])
    const [quantity,setQuantity] = useState(1)
    const location = useLocation()
    const { id } = useParams()
    console.log(location.pathname.split('/')[2])
    const foodEnpoint = location.pathname.split('/')[2]
    const { token } = useSelector((state) => state.auth)
    const products = useSelector((state) => state.cart)

    console.log(products)

    useEffect(() => {

        const fetchFoodType = async () => {

            const res = await fetch(`http://localhost:9000/product?category=${id}`, {

                headers: {
                    'Authorization': `Bearer ${token}`
                }

            })

            const data = await res.json()
            setFilteredFoods(data)
        }

        fetchFoodType()

    }, [foodEnpoint])


    const changeQuantity = (command)=>{
        if(command === "dec"){
            if(quantity === 1)return
            setQuantity(prev=>prev -1)
        }
        else if(quantity === "inc"){
            setQuantity(prev=>prev-1)
        }
    }

    const addToCart = () => {

        dispatch(addProduct({...foodDetails, quantity}))

      }


    return (

        <div className={classes.container}>
            <div className={classes.wrapper}>
                <div className={classes.leftSide}>
                    <img src={`http://localhost:5000/images/${filteredFoods?.img}`} />
                </div>

                <div className={classes.right}>
                    <h2 className={classes.title}>{filteredFoods?.title}</h2>
                    <div className={classes.price}>
                        Price: <span>$</span> {filteredFoods?.price}
                    </div>

                    <div className={classes.quantity}>
                        <button disabled={quantity === 1} onClick={() => changeQuantity('dec')}>-</button>
                        <span className={classes.quantityNumber}>{quantity}</span>
                        <button onClick={() => changeQuantity('inc')}>+</button>
                    </div>

                    <div className={classes.category}>
                        <h3>Category: </h3>
                        <span className={classes.categoryName}>{filteredFoods?.category}</span>
                    </div>

                    <div className={classes.productDesc}>
                        <div>Description: </div>
                        <p>
                            {filteredFoods?.desc?.length > 50 ? `${filteredFoods?.desc}`.slice(0, 50) : filteredFoods?.desc}
                        </p>
                    </div>

                    <button onClick={addToCart} className={classes.addToCart}>Add To Cart <AiOutlineShoppingCart /></button>
                </div>
            </div>
        </div>


    )
}
export default FoodDetails
