import React, { useState } from 'react';

import axios from 'axios';

 function QueryDisplay() {

  const [orderNumber, setOrderNumber] = useState('');

  const [query, setQuery] = useState(null);


  const handleOrderNumberChange = (event) => {

    setOrderNumber(event.target.value);

  };

  const handleFetchQuery = async () => {

    try {

      const response = await axios.get(`/aliens/fetch/query/${orderNumber}`);

      console.log(response,'data from backend')

      setQuery(response.data);

    } catch (error) {

      console.error('Error fetching query:', error);

    }
  };

  return (

    <div>

      <h2>Query Display</h2>

      <div>

        <input type="text" value={orderNumber} onChange={handleOrderNumberChange} />

        <button onClick={handleFetchQuery}>Fetch Query</button>

      </div>


      {query && (

        <pre>{JSON.stringify(query, null, 2)}</pre>
      )}
    </div>
  );
}

export default QueryDisplay;
