import React from "react";
import { classes } from './Create.module.css'
import { AiOutlineCloseCircle } from 'react-icons/ai'
import {useState} from 'react'

const Create = () => {

const [title,setTitle] = useState("");
const [desc,setDesc] = useState("")
const [Category,setCategory] = useState("")











    return (

        <div className={classes.container}>
            <div className={classes.wrapper}>
                <h2 className={classes.title}>Create Food</h2>
                <form onSubmit={handleCreateProduct} encType="multipart/form-data">
                    <div className={classes.inputWrapper}>
                        <label>Title: </label>
                        <input type="text"
                            placeholder="title"
                            className={classes.input}
                            onChange={(e) => setTitle(e.target.value)} />

                    </div>

                    <div className={classes.inputWrapper}>
                        <label>Description: </label>
                        <input type="text"
                            placeholder="Description"
                            className={classes.input}
                            onChange={(e) => setDesc(e.target.value)} />

                    </div>


                    <div className={classes.inputWrapper}>
                        <label>Category: </label>
                        <input type="text"
                            placeholder="Category"
                            className={classes.input}
                            onChange={(e) => setCategory(e.target.value)} />

                    </div>

                    <div className={classes.inputWrapperImage}>
                        <label htmlFor="image" className={classes.labelFileInput}>Image: <span>Upload here</span></label>
                        <input type="file"
                            id="image"
                            placeholder='Image...'
                            className={classes.input}
                            onChange={onChangeFile}
                            style={{ display: 'none' }}
                        />
                        {image && <p className={classes.imageName}>{image.name} <AiOutlineCloseCircle onClick={handleCloseImg} className={classes.closeIcon} /></p>}
                    </div>

                    <div className={classes.inputWrapper}>
                        <label>Price:</label>
                        <input type="number"
                            step={0.01}
                            placeholder="price"
                            className={classes.input}
                            onChange={(e) => setTitle(e.target.value)} />
                    </div>

                    <div className={classes.inputWrapper}>
                        <label>Review:</label>
                        <input type="number"
                        step={0.01}
                        min={1}
                        max={5}
                            placeholder="review"
                            className={classes.input}
                            onChange={(e) => setReview(e.target.value)} />

                    </div>

                </form>

            </div>

        </div>

    )
}

export default Create
