import {createSlice} from '@reduxjs/toolkit';

const initialSlice = {
    user: null,
    token: null,
}

export const authSlice = createSlice({
    name:'auth',

    initialState,

    reducers: {
        login:(state,action)=>{
            state.user = action.payload.others
            state.token = action.payload.token
        },
        register:(state,action)=>{
            state.user = action.payload.others
            state.token = action.payload.token
        },
        logout:(state)=>{
        state.user = null
        state.token = null
        localStorage.clear()
        }
    }
})
