import React from "react";
import classes from './FoodDetails.module.css';
import { Link, useLocation, useParams } from 'react-router-dom';
import { useState } from 'react'
import { useSelector } from "react-redux";
import { useEffect } from 'react';
import { addProduct } from '../../ReactRedux/authSlice'

const FoodDetails = () => {

    const [filteredFoods, setFilteredFoods] = useState([])
    const location = useLocation()
    const { id } = useParams()
    console.log(location.pathname.split('/')[2])
    const foodEnpoint = location.pathname.split('/')[2]
    const { token } = useSelector((state) => state.auth)
    const products = useSelector((state) => state.cart)

    console.log(products)

    useEffect(() => {

        const fetchFoodType = async () => {

            const res = await fetch(`http://localhost:9000/product?category=${id}`, {

                headers: {
                    'Authorization': `Bearer ${token}`
                }

            })

            const data = await res.json()
            setFilteredFoods(data)
        }

        fetchFoodType()

    }, [foodEnpoint])

    return (

        <div className={classes.container}>
            <div className={classes.wrapper}>
              <div className={classes.leftSide}>
            <img src={`http://localhost:5000/images/${filteredFoods?.img}`}/>

              </div>

              <div className={classes.right}>
              <h2 className={classes.title}>{filteredFoods?.title}</h2>
              <div className={classes.price}>
            Price: <span>$</span> {filteredFoods?.price}
          </div>
              </div>
            </div>
        </div>


    )
}
export default FoodDetails
