import React from "react";
import classes from './Login.module.css';
import {useState} from 'react'
 import {useDispatch} from 'react-redux'
import {Link, useNavigate} from 'react-router-dom'
import img from '../../assets/womaneating2.jpg'
import {store} from '../../ReactRedux/'

const Login = ()=>{
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("")
  const [error,setError] = useState(false)

 const dispatch = useDispatch()
const navigate = useNavigate()

const handleLogin = async(e)=>{
        e.preventDefault()

        try{
            const res = await fetch('http://localhost:5000/auth/login',{
       headers:{
             'Content-Type':'application/json'
           },
           method: 'POST',
           body: JSON.stringify({email,password})
            })

            const data = await res.json()
            console.log(data)

            dispatch(login(data)) // {userInfo, token}
            navigate("/")


        }catch(error){
         setError(true)
         setTimeout(()=>{
            setError(false)
         },3000)

        }
}

    return(

     <div className={classes.loginContainer}>
        <div className={classes.loginWrapper}>
            <div className={classes.loginLeftSide}>
           <img src={img} className={classes.leftImg}/>
            </div>

            <div className={classes.loginRightSide}>
                <h2 className={classes.title}>Login</h2>
                <form onSubmit={handleLogin} className={classes.loginForm}>
                    <input type="email" placeholder="emailId" onChange={(e)=> setEmail(e.target.value)}/>

                    <input type="password"  placeholder="password" onChange={(e) => setPassword(e.target.value)}/>

                    <button className={classes.subitBtn}>Login</button>
                    <p>Don't have account? <Link to="/signup">Sign up</Link></p>

                </form>

                {
                    error && <div className = {classes.errorMessage}>
                    Wrong credentials! Try different ones
                    </div>
                }

            </div>
        </div>

     </div>

    )
}

export default Login
