import React from "react";
import classes from './signup.module.css';
import img from '../../assets/womaneating.jpg'
import { useDispatch } from 'react-redux'
import { useState } from "react";
import {Link} from 'react-router'


const Signup = () =>{
    const [username,setUserName] = useState("")
    const [email, setEmail] = useState("")
    const [password,setPassword] = useState()
    const [error,setError] = useState(false)
    const dispatch = useDispatch()
    const navigate = useNavigate()


    const handleSignUp = async(e)=>{
        e.preventDefault()
        try{
            const res = await fetch('http://localhost:5000/auth/register',{

            headers:{
                'Content-Type':'application/json'

            },
            method:'POST',
            body: JSON.stringify({username,email,password})
            })


            const data = await res.json()
            dispatch(register(data))
            navigate('/')

        }catch(error){
            setError(true)
            setTimeout(() => {
              setError(false)
            }, 3000)
        }
    }


    return(
        <div className={classes.signUpContainer}>
            <div className={classes.signUpWrapper}>
                <div className={classes.signUpLeftSide}>
                    <img src={img} className={classes.leftImg}/>
                </div>
                <div className={classes.signUpRightSide}>
                    <h2 className={classes.title}>sign up</h2>
                    <form onChange={handleSignUp} className={classes.signUpForm}>
                        <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)}/>
                        <input type="email" placeholder="email" onChange={(e)=> setEmail(e.target.value)}/>
                        <input type="password" placeholder="password" onChange={(e)=> setPassword(e.target.value)}/>
                        <button className={classes.submitBtn}>SingUp</button>
                        <p>Already you have an account?<Link></Link> </p>
                    </form>
                </div>
            </div>
        </div>
    )
}
export default Signup
